import Headeradmin from "../Headeradmin/Headeradmin";
import s from "../AdminNews/Adminnews.module.css";
import React, {useEffect, useState} from "react";
import axios from "axios";
import {Link, NavLink} from 'react-router-dom';

const Adminnews = () => {
    const [news, setNews] = useState([])
    useEffect(() => {
        axios.get("http://localhost:4001/news").then(({data}) => {
            setNews(data)
        })
    }, [])
    console.log(news)


    const newnews = news.map(item => {
        return item.title
    })
    console.log(newnews)


    return (
        <div>
            <Headeradmin/>
            <div className={s.navigation}>
                <div className={s.otstup}>
                    <div className={s.item}>
                        <form>
                            <input type="text" placeholder="Введите запрос"/>
                            <button type="submit">Поиск</button>
                        </form>
                    </div>
                    <div></div>
                    <div className={s.buttonadd}><NavLink to="/add-news"> Добавить новость </NavLink>

                    </div>
                </div>
            </div>
                <div className={s.content}>
                    <table>
                        <thead>
                        <tr>
                            <th></th>
                            <th>Заголовок</th>
                            <th>Автор</th>
                            <th>Тег</th>
                            <th>Текст</th>
                            <th>Предпоказ</th>
                            <th>Дата</th>
                            <th>Изображения</th>
                        </tr>
                        </thead>
                        <tbody>
                        {news.map(item => {
                            return (

                                <tr>
                                    <td><input type="checkbox" name="choice"/></td>
                                    <td> {item.title}</td>
                                    <td>{item?.createdBy?.email}</td>
                                    <td> {item.tag}</td>
                                    <td>{item.text}</td>
                                    <td>{item.smallText}</td>
                                    <td>{item.date}</td>
                                    <td>{item.img}</td>
                                    <td><Link to={`/news/edit/${item._id}`}> Редактировать </Link></td>
                                </tr>
                            )
                        })}
                        </tbody>
                    </table>
                </div>
            </div>
            );
            }

            export default Adminnews;
