import Headeradmin from "../Headeradmin/Headeradmin";
import s from "../EditNews/Editnews.module.css";
import React, {useEffect, useState} from "react";
import axios from "axios";
import NewsForm from "../NewsForm/NewsForm";
import {useLocation, useParams} from "react-router-dom";

const EditNews = () => {


    const params = useParams()
    console.log(params)
    const [error, setError] = useState()
    const [news, setNews] = useState([])
    useEffect(() => {
        axios.get("http://localhost:4001/news").then(({data}) => {
            setNews(data)
        })
    },[]  )
    console.log(news)
    const editNews = news.find((item) => item._id === params.newsId)

    const onSubmit = async(data) => {

        const response = await axios.put("http://localhost:4001/news",data, {headers:{"x-access-token":localStorage.getItem("token")}}).catch(() => setError("Произошла ошибка"))
        if (response.statusText === "OK") {
            const {data} = response
            console.log(data)
        }
    }

    return (
        <div>
            <Headeradmin/>
            <NewsForm onSubmit={onSubmit} error={error} />
        </div>
    );
}

export default EditNews;
