import React, {useState} from "react";
import s from "./Registration.module.css";
import Headeradminlog from "../Headeradmin/Headeradminlog";
import {useNavigate} from "react-router-dom";
import axios from "axios";


const Registration = () => {
    const [first_name, setFirst_name] = useState("")
    const [last_name, setLastName] = useState("")
    const [login, setLogin] = useState("")
    const [password, setPassword] = useState("")
    const [error, setError] = useState()
    const navigate = useNavigate();
    const handleChangeFirst_name = (e) => {
        const value = e.target.value;
        setFirst_name(value)
    }
    const handleChangeLast_name= (e) => {
        const value = e.target.value;
        setLastName(value)
    }
    const handleChangeLogin = (e) => {
        const value = e.target.value;
        setLogin(value)
    }
    const handleChangePassword = (e) => {
        const value = e.target.value;
        setPassword(value)
    }
    const onSubmit = async(e) => {
        e.preventDefault();
        const response = await axios.post("http://localhost:4001/login",{first_name, last_name, email:login , password}).catch(() => setError("Неверные данные для входа"))
        if (response.statusText === "OK") {
            const {data} = response
            localStorage.setItem("token", data.token)
            navigate("/adminnews")
        }
    }

    return <div >
        <Headeradminlog />
        <div className={s.registration}>
            Заполните форму:
            <form onSubmit={onSubmit}>
                <input type="text" value={first_name} onChange={handleChangeFirst_name}  placeholder="Имя"  />
                <input type="password" value={last_name} onChange={handleChangeLast_name}   placeholder="Фамилия" />
                <input type="email" value={login} onChange={handleChangeLogin} placeholder="E-mail"  />
                <input type="password"  value={password} onChange={handleChangePassword}    placeholder="Пароль" />
                             <br/> <br/>
                <button type="submit">Зарегистрироваться</button>

            </form>
        </div>
    </div>
}


export default Registration ;
